import axios from "axios";

import React, { useLayoutEffect, useEffect, useState,useContext,useCallback } from "react";
import { ScrollView,StatusBar,Button, ImageBackground, Text, View, Image, TouchableHighlight,TextInput,SafeAreaView,StyleSheet  } from "react-native";
import styles from "./styles";
//import { categories } from "../../data/dataArrays";
import { getNumberOfRecipes } from "../../data/MockDataAPI";
import MenuImage from "../../components/MenuImage/MenuImage";
import Ip from "../../Ip";
import NativeUploady, {
  UploadyContext,
  useItemFinishListener,
  useItemStartListener,
  useItemErrorListener,
} from "@rpldy/native-uploady";

export default function CategoriesScreen(props) {
  const {ip} = Ip();
  const { navigation } = props;
  const [categories, setCategories] = useState([]);
 
 /* const loadd = () => {
    async function f() {
      axios
      .get('http://'+ip+'/api/categorieAnnonces')
      .then(function (response) {
        //console.log(response.data.categorieAnnonce[0]);
      // setCategories(response.data.categorieAnnonce);
      })
      .catch(function (error) {
        // handle error
        alert(error.message);
      });
    }
    f();
  };*/
  const [membre_id, setmembre_id] = useState('membre_id');
  const [type_annonce_id, settype_annonce_id] = useState('type_annonce_id');
  const [categorie_annonce_id, setcategorie_annonce_id] = useState('categorie_annonce_id');
  const [commentaire_id, setcommentaire_id] = useState('commentaire_id');

  const [titre, setTitre] = useState('titre');
  const [texte, setTexte] = useState('texte');
  const [nbr_vue, setNbrVue] = useState('nbr_vue');
 
  const [etat, setEtat] = useState('etat');
  const [img, setImg] = useState('Img');
  const [classement, setClassement] = useState('classement');
 
  const [visible, setVisible] = useState('visible');
  const [publie, setPublie] = useState('publie');
  const [photosArray, setPhotosArray] = useState('photosArray');
 
//
const Upload = () => {
  const [uploadUrl, setUploadUrl] = useState(false);
  const uploadyContext = useContext(UploadyContext);
  const pickFile = useCallback(async () => {
    try {
     console.log('click !!!! ');
   /*  console.log(categorie_annonce_id);
     console.log(classement);
     console.log(commentaire_id);*/
     axios
     .post('http://'+ip+'/api/annonces', {
      membre_id: 1,
      type_annonce_id: 1,
      categorie_annonce_id: 1,
      commentaire_id: 1,
      titre: "titre",
      texte: "texte",
      nbr_vue: "nbr_vue",
      etat: "etat",
      classement: "classement",
      visible:"true",
      publie: "true",
     })
     .then(function (response) {
      console.log('bien ajouter !!!');
     }).catch(function (error) {
                    //alert(response.message);
                   console.log(error);
                });
   
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <View>
      <Button title="Choose File" onPress={pickFile} />
    
    </View>
  );
};
//

  useEffect(() => {
     // loadd();
  }, []);

/************* */
  useLayoutEffect(() => {
    navigation.setOptions({
      headerTitleStyle: {
        fontWeight: "bold",
        textAlign: "center",
        alignSelf: "center",
        flex: 1,
      },
      headerLeft: () => (
        <MenuImage
          onPress={() => {
            navigation.openDrawer();
          }}
        />
      ),
      headerRight: () => <View />,
    });
  }, []);

  const onPressCategory = (item) => {
    const title = item.name;
    const category = item;
    navigation.navigate("RecipesList", { category, title });
  };

  const renderCategory = ({ item }) => (
    <TouchableHighlight underlayColor="rgba(73,182,77,0.9)" onPress={() => onPressCategory(item)}>
      <View style={styles.categoriesItemContainer}>
        <Image style={styles.categoriesPhoto} source={{ uri: item.image }} />
        <Text style={styles.categoriesName}>{item.libelle}</Text>
        <Text style={styles.categoriesInfo}>{item.desc} recipes</Text>
      </View>
    </TouchableHighlight>
  );

  return (
    <ScrollView>
    
        <SafeAreaView>
        <Text style={stylezs.text}>categorie_annonce_id</Text>
       <TextInput
        name='categorie_annonce_id'
        style={stylezs.input}
        onChangeText={setcategorie_annonce_id}
        value={categorie_annonce_id}
      />
       <Text style={stylezs.text}>classement</Text>
       <TextInput
       name='classement'
        style={stylezs.input}
        onChangeText={setClassement}
        value={classement}
      />
       <Text style={stylezs.text}>commentaire_id</Text>
       <TextInput
       name='commentaire_id'
        style={stylezs.input}
        onChangeText={setcommentaire_id}
        value={commentaire_id}
      />
          <Text style={stylezs.text}>membre_id</Text>
       <TextInput
        name='membre_id'
        style={stylezs.input}
        onChangeText={setmembre_id}
        value={membre_id}
      />
       <Text style={stylezs.text}>type_annonce_id</Text>
       <TextInput
       name='type_annonce_id'
        style={stylezs.input}
        onChangeText={settype_annonce_id}
        value={type_annonce_id}
      />
       <Text style={stylezs.text}>titre</Text>
       <TextInput
       name='titre'
        style={stylezs.input}
        onChangeText={setTitre}
        value={titre}
      />
          <Text style={stylezs.text}>texte</Text>
       <TextInput
        name='texte'
        style={stylezs.input}
        onChangeText={setTexte}
        value={texte}
      />
       <Text style={stylezs.text}>nbr_vue</Text>
       <TextInput
       name='nbr_vue'
        style={stylezs.input}
        onChangeText={setNbrVue}
        value={nbr_vue}
      />
       <Text style={stylezs.text}>etat</Text>
       <TextInput
       name='etat'
        style={stylezs.input}
        onChangeText={setEtat}
        value={etat}
      />
          <Text style={stylezs.text}>img</Text>
       <TextInput
        name='img'
        style={stylezs.input}
        onChangeText={setImg}
        value={img}
      />
       <Text style={stylezs.text}>visible</Text>
       <TextInput
       name='visible'
        style={stylezs.input}
        onChangeText={setVisible}
        value={visible}
      />
       <Text style={stylezs.text}>publie</Text>
       <TextInput
       name='publie'
        style={stylezs.input}
        onChangeText={setPublie}
        value={publie}
      />
    <View style={styles.body}>
              <View style={styles.sectionContainer}>
                <Text style={styles.sectionTitle}>Envoier</Text>
              </View>
            </View>
      </SafeAreaView>
      <Upload/>
     
    </ScrollView>
  );
 
}
const stylezs = StyleSheet.create({
  input: {
    height: 40,
    marginLeft: 20,
    marginRight: 10,
    borderWidth: 1,
    padding: 10,
  },
  text: {
    height: 40,
    marginLeft: 10,
    padding: 10,
  },
});
