import axios from "axios";
import Select from 'react-select'
import React, { useLayoutEffect, useEffect, useState,useContext,useCallback } from "react";
import { ScrollView,StatusBar,Button, ImageBackground, Text, View, Image, TouchableHighlight,TextInput,SafeAreaView,StyleSheet  } from "react-native";
import styles from "./styles";
//import { categories } from "../../data/dataArrays";
import { getNumberOfRecipes } from "../../data/MockDataAPI";
import MenuImage from "../../components/MenuImage/MenuImage";
import Ip from "../../Ip";
import NativeUploady, {
  UploadyContext,
  useItemFinishListener,
  useItemStartListener,
  useItemErrorListener,
} from "@rpldy/native-uploady";

export default function CategoriesScreen(props) {
  //
  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]
  //
  const {ip} = Ip();
  const { navigation } = props;
  const [categories, setCategories] = useState([]);
  const [cat , setCat]=useState([]);
  /*const loadd = () => {
    async function f() {
      axios
      .get('http://'+ip+'/api/lista')
      .then(function (response) {
        setCat(response.data.cat);
     //   console.log('************************************************');
      //  console.log(response.data.cat);
      // setCategories(response.data.categorieAnnonce);
      })
      .catch(function (error) {
        // handle error
        alert(error.message);
      });
    }
    f();
  };*/
  const [membre_id, setmembre_id] = useState('2');
  const [type_annonce_id, settype_annonce_id] = useState('2');
  const [categorie_annonce_id, setcategorie_annonce_id] = useState('2');
  const [commentaire_id, setcommentaire_id] = useState('2');

  const [titre, setTitre] = useState('titre');
  const [texte, setTexte] = useState('texte');
  const [nbr_vue, setNbrVue] = useState('100000');
 
  const [etat, setEtat] = useState('etat');
  const [img, setImg] = useState('Img');
  const [classement, setClassement] = useState('10');
 
  const [visible, setVisible] = useState('1');
  const [publie, setPublie] = useState('1');
  const [photosArray, setphotosArray] = useState('photosArray');
 
//
const Upload = () => {
  const [uploadUrl, setUploadUrl] = useState(false);
  const uploadyContext = useContext(UploadyContext);
  const pickFile = useCallback(async () => {
    try {
     console.log('click !!!! ');
   /*  console.log(categorie_annonce_id);
     console.log(classement);
     console.log(commentaire_id);*/
     axios
     .post('http://'+ip+'/api/annonces', {
      membre_id: membre_id,
      type_annonce_id: type_annonce_id,
      categorie_annonce_id: categorie_annonce_id,
      commentaire_id: commentaire_id,
      titre: titre,
      texte: texte,
      nbr_vue: nbr_vue,
      etat: etat,
      classement: classement,
      visible: visible,
      publie: publie,
      photosArray: "photosArray"
     })
     .then(function (response) {
      alert(response.data);
     }).catch(function (error) {
                    //alert(response.message);
                    alert(error);
                });
   
    } catch (err) {
      console.log(err);
    }
  }, []);

  return (
    <View>
      <Button title="Choose File" onPress={pickFile} />
    
    </View>
  );
};
//

  useEffect(() => {
      //loadd();
  }, []);

/************* */
  useLayoutEffect(() => {
    navigation.setOptions({
      headerTitleStyle: {
        fontWeight: "bold",
        textAlign: "center",
        alignSelf: "center",
        flex: 1,
      },
      headerLeft: () => (
        <MenuImage
          onPress={() => {
            navigation.openDrawer();
          }}
        />
      ),
      headerRight: () => <View />,
    });
  }, []);

  const onPressCategory = (item) => {
    const title = item.name;
    const category = item;
    navigation.navigate("RecipesList", { category, title });
  };

  const renderCategory = ({ item }) => (
    <TouchableHighlight underlayColor="rgba(73,182,77,0.9)" onPress={() => onPressCategory(item)}>
    
    </TouchableHighlight>
  );

  return (
    <ScrollView>
    
        <SafeAreaView>
        <Text 
        >categorie_annonce_id</Text>
       <TextInput
        name='categorie_annonce_id'
       
        
        onChangeText={setcategorie_annonce_id}
        value={categorie_annonce_id}
      />
       <Select options={options} />
       <Text
       >classement</Text>
       <TextInput
       name='classement'
      
       
        onChangeText={setClassement}
        value={classement}
      />
       <Text >commentaire_id</Text>
       <TextInput
       name='commentaire_id'
      
        onChangeText={setcommentaire_id}
        value={commentaire_id}
      />
          <Text >membre_id</Text>
       <TextInput
        name='membre_id'
       
        onChangeText={setmembre_id}
        value={membre_id}
      />
       <Text>type_annonce_id</Text>
       <TextInput
       name='type_annonce_id'
        onChangeText={settype_annonce_id}
        value={type_annonce_id}
      />
       <Text >titre</Text>
       <TextInput
       name='titre'
        onChangeText={setTitre}
        value={titre}
      />
          <Text >texte</Text>
       <TextInput
        name='texte'
        onChangeText={setTexte}
        value={texte}
      />
       <Text >nbr_vue</Text>
       <TextInput
       name='nbr_vue'
        onChangeText={setNbrVue}
        value={nbr_vue}
      />
       <Text >etat</Text>
       <TextInput
       name='etat'
        onChangeText={setEtat}
        value={etat}
      />
          <Text >photosArray</Text>
       <TextInput
        name='photosArray'
        onChangeText={setphotosArray}
        value={photosArray}
      />
       <Text >visible</Text>
       <TextInput
       name='visible'
      
        onChangeText={setVisible}
        value={visible}
      />
       <Text >publie</Text>
       <TextInput
       name='publie'
      
        onChangeText={setPublie}
        value={publie}
      />
   
      </SafeAreaView>
      <Upload/>
     
    </ScrollView>
  );
 
}
const stylezs = StyleSheet.create({
  input: {
    height: 40,
    marginLeft: 20,
    marginRight: 10,
    borderWidth: 1,
    padding: 10,
  },
  text: {
    height: 40,
    marginLeft: 10,
    padding: 10,
  },
});
